import { useEffect, useState } from "react";
import db from "../Database"; 
import { Link } from "react-router-dom";




function Search() {
    const breeds = db.breeds;
    const [searchInput, setSearchInput] = useState("");
    const [showSearchResults, setShowSearchResults] = useState(false);
    const controller = new AbortController();
    // const url = `https://api.thecatapi.com/v1/breeds/abys`;
    const url = `https://swapi.dev/api/people/1`;
const api_key = "DEMO_API_KEY"
const [searchResults, setSearchResults] = useState([]);
useEffect(() => {
    async function getData() {
    const response = await fetch(url);
    const data = await response.json();
    setSearchResults(data);
    }

    getData();
    console.log("*******************");
    console.log(searchResults,"searchResults");
    return () => {controller.abort();}

},[]);


    return(
        <div>
            <label for="search">Find your puppy here</label>
            <input
                id="search"
             value={searchInput}
             onChange={(e) => setSearchInput(e.target.value)}
            />

            <button className="btn btn-danger"
                    onClick={() => setShowSearchResults(!showSearchResults)}
            >Search!</button>



                {showSearchResults && <h1>This is the search results for you</h1> }

                {showSearchResults &&
                <div className="list-group"> 
                    {breeds.map((breed) => ( 
                    <Link key={breed.id} to={`/detail/${breed.id}`} className="list-group-item"> 
                    {breed.id}<br/>
                    {breed.name} 
                    </Link> ))} 
                </div>
                    }


                    {/* {searchResults} */}







        </div>

    );
 };
 export default Search;
