import React, { useState } from "react";
import { useNavigate } from 'react-router-dom'; // 导入 useNavigate

export const Register = (props) => {
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [name, setName] = useState('');
    const [role, setRole] = useState('customer'); // 默认角色设置为 'customer'
    const navigate = useNavigate(); // 创建 navigate 实例

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(email, role);
        // 添加您的注册逻辑
        // 如果注册成功:
        navigate('/login'); // 假设注册成功后跳转到登录页面
    }

    return (
        <div className="auth-form-container">
            <h2>Register</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input value={name} name="name" onChange={(e) => setName(e.target.value)} id="name" placeholder="Name" />

                <label htmlFor="email">Email</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@gmail.com" id="email" name="email" />

                <label htmlFor="password">Password</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="********" id="password" name="password" />

                <label htmlFor="role">Role</label>
                <select value={role} onChange={(e) => setRole(e.target.value)} id="role" name="role">
                    <option value="admin">Admin</option>
                    <option value="business">Business</option>
                    <option value="customer">Customer</option>
                </select>

                <br />

                <button type="submit">Register</button>
            </form>
            <button className="link-btn" onClick={() => props.onFormSwitch('login')}>Already have an account? Login here.</button>
        </div>
    )
}
