import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import defaultAvatar from './dog.jpg';
import './Profile.css';
import Posts from './Posts';
import Favorites from './Favorites';

const UserProfile = () => {
    const { authUser } = useAuth(); // Use useAuth to get the authenticated user
    const navigate = useNavigate();
    const [userInfo, setUserInfo] = useState({
        avatar: defaultAvatar,
        name: '',
        email: '',
        birthDate: '',
        address: ''
    });
    const [editMode, setEditMode] = useState(false);
    const [followers, setFollowers] = useState([]);
    const [following, setFollowing] = useState([]);


    useEffect(() => {
        console.log('Current authUser:', authUser); // Debugging line to check the authUser state

        if (!authUser || !authUser.userID) {
            console.log('Redirecting to login because authUser or userID is not found');
            navigate('/login');
            return;
        }

        axios.get(`http://localhost:3001/profile/${authUser.userID}`)
            .then(response => {
                setUserInfo(response.data);
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });

        axios.get(`http://localhost:3001/users/${authUser.userID}/followers`)
            .then(response => {
                setFollowers(response.data);
            })
            .catch(error => {
                console.error('Error fetching followers:', error);
            });

        // Fetch following
        axios.get(`http://localhost:3001/users/${authUser.userID}/following`)
            .then(response => {
                setFollowing(response.data);
            })
            .catch(error => {
                console.error('Error fetching following:', error);
            });
    }, [authUser, navigate]);






    const dummyPosts = [
        { id: 1, content: 'First post content' },
        { id: 2, content: 'Second post content' }
        // ... more posts data
    ];
    const dummyFavorites = [
        { id: 1, item: 'Favorite Item 1' },
        { id: 2, item: 'Favorite Item 2' }
        // ... more favorites data
    ];

    const refreshFollowing = () => {
        axios.get(`http://localhost:3001/users/${authUser.userID}/following`)
            .then(response => {
                setFollowing(response.data);
            })
            .catch(error => {
                console.error('Error refreshing following:', error);
            });
    };

    // Update followers list after follow/unfollow action
    const refreshFollowers = () => {
        axios.get(`http://localhost:3001/users/${authUser.userID}/followers`)
            .then(response => {
                setFollowers(response.data);
            })
            .catch(error => {
                console.error('Error refreshing followers:', error);
            });
    };

    const handleFollow = (followedUserId) => {
        axios.post(`http://localhost:3001/api/users/${followedUserId}/follows`, {}, {
            withCredentials: true
        })
        .then(response => {
            // Optionally, refresh followers/following list here
            console.log('Followed successfully');
        })
        .catch(error => {
            console.error('Error following user:', error);
        });
    };

    const handleUnfollow = (followedUserId) => {
        axios.delete(`http://localhost:3001/api/users/${followedUserId}/follows`, {
            withCredentials: true
        })
        .then(response => {
            // Optionally, refresh followers/following list here
            console.log('Unfollowed successfully');
        })
        .catch(error => {
            console.error('Error unfollowing user:', error);
        });
    };



    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();

        axios.put(`http://localhost:3001/updateProfile/${authUser.userID}`, userInfo)
          .then(response => {
            console.log("Profile updated successfully:", response.data);
          })
          .catch(error => {
            console.error('Error updating profile:', error);
          });

        setEditMode(false);
    };


    const isBirthDateEmpty = !userInfo.birthDate;
    const isAddressEmpty = !userInfo.address;

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">
                    <img src={userInfo.avatar || 'path/to/default/avatar.jpg'} alt="User Avatar" className="avatar-image" />
                <p>Name: {userInfo.name}</p>
                <p>Email: {userInfo.email}</p>
                {isBirthDateEmpty && <p className="reminder">Birth Date: Not Provided</p>}
                {!isBirthDateEmpty && <p>Birth Date: {userInfo.birthDate}</p>}
                {isAddressEmpty && <p className="reminder">Address: Not Provided</p>}
                {!isAddressEmpty && <p>Address: {userInfo.address}</p>}
                        <button onClick={() => setEditMode(true)}>Edit</button>
                    </div>

                    <div className="user-interactions-container">
                    <div className="user-section">
    <h3>Following</h3>
    {following.map(user => (
        <div key={user.userID} className="following-user">
            <span>{user.name}</span>
            <button onClick={() => {
                handleUnfollow(user.userID);
                refreshFollowing(); // Refresh following list after unfollow
            }}>Unfollow</button>
        </div>
    ))}
</div>

<div className="user-section">
    <h3>Followers</h3>
    {followers.map(user => (
        <div key={user.userID} className="follower-user">
            <span>{user.name}</span>
            <button onClick={() => {
                handleFollow(user.userID);
                refreshFollowers(); // Refresh followers list after follow
            }}>Follow</button>
        </div>
    ))}
</div>



                        <div className="user-section">
                            <h3>Posts</h3>
                            <Posts postList={dummyPosts} />
                        </div>

                        <div className="user-section">
                            <h3>Favorites</h3>
                            <Favorites favoritesList={dummyFavorites} />
                        </div>
                    </div>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <input type="file" />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <div>
                        <button type="submit">Save Changes</button>
                        <button type="button" onClick={() => setEditMode(false)}>Return to Profile</button>
                    </div>
                </form>
            )}

        </div>
    );
};

export default UserProfile;
