import React, { useState } from 'react';

const Profile = () => {
    const [userInfo, setUserInfo] = useState({
        avatar: '', // URL of the user's avatar
        name: '', // User's name
        email: '', // Email address
        birthDate: '', // Birth date
        address: '' // Residential address
    });

    // Handles changes to the input fields
    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    // Handles avatar changes
    const handleAvatarChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            // Assuming the backend logic to upload and get the URL of the avatar
            // For now, we're just displaying the selected image as a preview
            const reader = new FileReader();
            reader.onload = (e) => {
                setUserInfo({ ...userInfo, avatar: e.target.result });
            };
            reader.readAsDataURL(event.target.files[0]);
        }
    };

    // Handles the form submission
    const handleFormSubmit = (event) => {
        event.preventDefault();
        // Logic to submit the form, including the new avatar
        console.log(userInfo);
    };

    return (
        <div className="profile-container">
            <form onSubmit={handleFormSubmit}>
                <div className="avatar-section">
                    <img src={userInfo.avatar} alt="User Avatar" />
                    <input
                        type="file"
                        onChange={handleAvatarChange}
                    />
                </div>
                <input
                    type="text"
                    name="name"
                    value={userInfo.name}
                    onChange={handleInputChange}
                    placeholder="Name"
                />
                <input
                    type="email"
                    name="email"
                    value={userInfo.email}
                    onChange={handleInputChange}
                    placeholder="Email"
                />
                <input
                    type="date"
                    name="birthDate"
                    value={userInfo.birthDate}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="address"
                    value={userInfo.address}
                    onChange={handleInputChange}
                    placeholder="Address"
                />
                <button type="submit">Save Changes</button>
            </form>

            <div className="user-cards">
                <div className="card">
                    <a href="#">
                        <h3>Following</h3>
                    </a>
                </div>
                <div className="card">
                    <a href="#">
                        <h3>Followers</h3>
                    </a>
                </div>
                <div className="card">
                    <a href="#">
                        <h3>Reviews</h3>
                    </a>
                </div>
                <div className="card">
                    <a href="#">
                        <h3>Favorites</h3>
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Profile;
