import React, { useState, useEffect } from 'react';
import defaultAvatar from './dog.jpg';
import './Profile.css';
import Posts from './Posts'; // Assuming you have a Posts component
import Likes from './Likes'; // Assuming you have a Likes component
import Favorites from './Favorites';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import 'bootstrap-icons/font/bootstrap-icons.css';


const UserProfile = () => {
    const UserProfile = () => {
        const { profileId } = useParams(); // Fetching profileId from URL
        const [userInfo, setUserInfo] = useState({
            avatar: defaultAvatar,
            name: '',
            email: '',
            birthDate: '',
            address: ''
        });
        const [editMode, setEditMode] = useState(false);
        const [hasLiked, setHasLiked] = useState(false);

        // Add useEffect to fetch user data when the component mounts or profileId changes
        useEffect(() => {
            axios.get(`http://localhost:3001/userProfile/${profileId}`)
                .then(response => {
                    setUserInfo(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user data:', error);
                });
        }, [profileId]);

    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();

        axios.put(`http://localhost:3001/updateProfile/${userId}`, userInfo)
          .then(response => {
            console.log("Profile updated successfully:", response.data);
          })
          .catch(error => {
            console.error('Error updating profile:', error);
          });

        setEditMode(false);
      };


    const handleLike = () => {
          // Logic to handle the like action
          setHasLiked(true);
          // Add API call or other actions to record the like
    };


    const dummyPosts = [
        { id: 1, userId: 'user1', content: 'Post content 1' },
        { id: 2, userId: 'user1', content: 'Post content 2' },
        // Add more dummy posts as needed
    ];

    const dummyLikes = [
        { id: 1, content: 'User123 liked your post' },
        { id: 2, content: 'User456 liked your post' },
        // Add more dummy likes as needed
    ];

    const dummyFavorites = [
        { id: 1, title: 'Favorite 1' },
        { id: 2, title: 'Favorite 2' },
    ];


    const isBirthDateEmpty = !userInfo.birthDate;
    const isAddressEmpty = !userInfo.address;

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">

                    <img src={userInfo.avatar || 'path/to/default/avatar.jpg'} alt="User Avatar" className="avatar-image" />
                    <p>Name: {userInfo.name}</p>
                    <p>Email: {userInfo.email}</p>
                    {isBirthDateEmpty && <p className="reminder">Birth Date: Not Provided</p>}
                    {!isBirthDateEmpty && <p>Birth Date: {userInfo.birthDate}</p>}
                    {isAddressEmpty && <p className="reminder">Address: Not Provided</p>}
                    {!isAddressEmpty && <p>Address: {userInfo.address}</p>}
                        <button onClick={() => setEditMode(true)}>Edit</button>
                    </div>

                    <div className="user-interactions-container">
                        <div className="user-section">
                            <h3>Posts</h3>
                            <Posts postList={dummyPosts} />
                        </div>

                        <div className="user-section">
                            <h3>Likes</h3>
                            <button className={`like-button ${hasLiked ? 'liked' : ''}`} onClick={handleLike}>
                                <i className={`bi ${hasLiked ? 'bi-heart-fill' : 'bi-heart'}`}></i> {hasLiked ? 'Liked' : 'Like'}
                            </button>


                            <Likes likeList={dummyLikes} />
                        </div>

                        <div className="user-section">
                            <h3>Favorites</h3>
                            <Favorites favoritesList={dummyFavorites} />
                        </div>
                    </div>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <input type="file" />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <div>
                        <button type="submit">Save Changes</button>
                        <button type="button" onClick={() => setEditMode(false)}>Return to Profile</button>
                    </div>
                </form>
            )}

        </div>
    );
};
};

export default UserProfile;
