import React, { useEffect, useState } from 'react';
import defaultAvatar from './dog.jpg';
import './Profile.css';

const UserProfile = () => {
    const [userInfo, setUserInfo] = useState({
        avatar: defaultAvatar,
        name: '',
        email: '',
        birthDate: '',
        address: '' 
    });
    const [editMode, setEditMode] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        fetch('/profile', {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => response.json())
        .then(data => setUserInfo({ ...userInfo, ...data }))
        .catch(error => console.error('Error fetching user data:', error));
    }, []);

    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        const token = localStorage.getItem('jwtToken');
        fetch('/profile', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify(userInfo)
        })
        .then(response => response.json())
        .then(data => {
            console.log('Profile updated:', data);
            setEditMode(false);
            setUserInfo({ ...userInfo, ...data.user }); // 更新状态以反映最新的用户信息
        })
        .catch(error => console.error('Error updating profile:', error));
    };


    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('avatar', file);

        const token = localStorage.getItem('jwtToken');
        fetch('/upload-avatar', {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${token}`
            },
            body: formData
        })
        .then(response => response.json())
        .then(data => {
            console.log('Avatar uploaded:', data);
            setUserInfo({ ...userInfo, avatar: data.avatarUrl }); // 假设后端返回新头像的URL
        })
        .catch(error => console.error('Error uploading avatar:', error));
    };

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <p>Name: {userInfo.name}</p>
                        <p>Email: {userInfo.email}</p>
                        <p>Birth Date: {userInfo.birthDate}</p>
                        <p>Address: {userInfo.address}</p>
                    </div>
                    <button onClick={() => setEditMode(true)}>Edit</button>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <input type="file" onChange={handleImageUpload} />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <button type="submit">Save Changes</button>
                </form>
            )}
        </div>
    );
};

export default UserProfile;
