import React, { useState } from 'react';
import defaultAvatar from './dog.jpg';
import './Profile.css';

const UserProfile = () => {
    const [userInfo, setUserInfo] = useState({
        avatar: defaultAvatar,
        name: 'John Doe',
        email: 'johndoe@example.com',
        birthDate: '1990-01-01',
        address: '123 Main St, Anytown'
    });
    const [editMode, setEditMode] = useState(false);

    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        setEditMode(false);
    };

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <p>Name: {userInfo.name}</p>
                        <p>Email: {userInfo.email}</p>
                        <p>Birth Date: {userInfo.birthDate}</p>
                        <p>Address: {userInfo.address}</p>
                    </div>
                    <button onClick={() => setEditMode(true)}>Edit</button>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <input type="file" />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <div>
                        <button type="submit">Save Changes</button>
                        <button type="button" onClick={() => setEditMode(false)}>Return to Profile</button>
                    </div>
                </form>
            )}

            <div className="user-cards">
                <div className="card"><a href="#"><h3>Following</h3></a></div>
                <div className="card"><a href="#"><h3>Followers</h3></a></div>
                <div className="card"><a href="#"><h3>Reviews</h3></a></div>
                <div className="card"><a href="#"><h3>Favorites</h3></a></div>
            </div>
        </div>
    );
};

export default UserProfile;
