import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import defaultAvatar from './dog.jpg';
import './Profile.css';

const OtherUserProfile = () => {
    const [userInfo, setUserInfo] = useState({
        avatar: defaultAvatar,
        name: '',
        email: '',
        birthDate: '',
        address: '' 
    });
    const { profileId } = useParams(); // 从URL获取profileId

    useEffect(() => {
        fetch(`/profile/${profileId}`)
        .then(response => response.json())
        .then(data => setUserInfo({ ...userInfo, ...data }))
        .catch(error => console.error('Error fetching user data:', error));
    }, [profileId]);

    return (
        <div className="profile-container">
            <div className="user-info">
                <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                <p>Name: {userInfo.name}</p>
                <p>Email: {userInfo.email}</p>
                <p>Birth Date: {userInfo.birthDate}</p>
                <p>Address: {userInfo.address}</p>
            </div>
            {/* 其他用户信息的展示 */}
        </div>
    );
};

export default OtherUserProfile;
