import React from 'react';
import { useAuth } from '../AuthContext'; // Corrected import statement
import { Link } from 'react-router-dom'; // Import Link from React Router

function Home() {
    const { authUser } = useAuth(); // Use useAuth to get the authUser

    // Check if the user is logged in and has a userID
    const isUserLoggedIn = authUser && authUser.userID;

    // Check if the user is an admin
    const isAdmin = isUserLoggedIn && authUser.role === 'admin';

    return (
        <div>
            {isAdmin ? (
                <h2>Hi admin</h2>
            ) : (
                <h2>Hi customer</h2>
            )}
            <p>Welcome to the Home page!</p>

            {/* Conditionally render Profile button */}
            {isUserLoggedIn && (
                <Link to={`/profile/${authUser.userID}`}>Go to Profile</Link>
            )}
        </div>
    );
}


export default Home;
