import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext'; // Ensure the path is correct

function Home() {
    const { authUser, logout } = useAuth(); // Use logout from useAuth
    const navigate = useNavigate();

    // Function to handle logout
    const handleLogout = () => {
        logout();
        // Redirect to login page or home page after logout
        navigate('/login');
    };

    // Function to navigate to the user's profile
    const goToProfile = () => {
        navigate(`/profile/${authUser.userID}`);
    };

    return (
        <div>
            <h2>Welcome to the Home page!</h2>
            {authUser && authUser.userID ? (
                <>
                    <p>Hi {authUser.user}, you are logged in.</p>
                    <button onClick={goToProfile}>Go to Profile</button>
                    <button onClick={handleLogout}>Logout</button>
                </>
            ) : (
                <p>You are not logged in.</p>
            )}
        </div>
    );
}

export default Home;
