import React, { useState } from 'react';
import defaultAvatar from './dog.jpg';
import './Profile.css';
import Following from './Following';
import Followers from './Followers';
import Reviews from './Reviews';
import Favorites from './Favorites';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const UserProfile = () => {
    const location = useLocation();
    const userId = location.state?.userId;
    const [userInfo, setUserInfo] = useState({
        avatar: defaultAvatar,
        name: '',
        email: '',
        birthDate: '',
        address: ''
    });
    const [editMode, setEditMode] = useState(false);

    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();

        aaxios.put(`http://localhost:3001/updateProfile/${userId}`, userInfo)
          .then(response => {
            console.log("Profile updated successfully:", response.data);
            // You can add more logic here, like showing a success message
          })
          .catch(error => {
            console.error('Error updating profile:', error);
            // Handle the error here, perhaps show an error message
          });

        setEditMode(false);
      };


    const dummyFollowing = [
        { id: 1, name: 'User 1' },
        { id: 2, name: 'User 2' },
        { id: 3, name: 'User 3' },
        { id: 4, name: 'User 4' },
    ];

    const dummyFollowers = [
        { id: 5, name: 'User 5' },
        { id: 6, name: 'User 6' },
    ];

    const dummyReviews = [
        { id: 1, summary: 'Review 1' },
        { id: 2, summary: 'Review 2' },
    ];

    const dummyFavorites = [
        { id: 1, title: 'Favorite 1' },
        { id: 2, title: 'Favorite 2' },
    ];


    const isBirthDateEmpty = !userInfo.birthDate;
    const isAddressEmpty = !userInfo.address;

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <p>Name: {userInfo.name}</p>
                        <p>Email: {userInfo.email}</p>
                        {isBirthDateEmpty && <p className="reminder">Please add your Birth Date</p>}
                        {!isBirthDateEmpty && <p>Birth Date: {userInfo.birthDate}</p>}
                        {isAddressEmpty && <p className="reminder">Please add your Address</p>}
                        {!isAddressEmpty && <p>Address: {userInfo.address}</p>}
                        <button onClick={() => setEditMode(true)}>Edit</button>
                    </div>

                    <div className="user-interactions-container">
                        <div className="user-section">
                            <h3>Following</h3>
                            <Following followingList={dummyFollowing} />
                        </div>

                        <div className="user-section">
                            <h3>Followers</h3>
                            <Followers followersList={dummyFollowers} />
                        </div>

                        <div className="user-section">
                            <h3>Reviews</h3>
                            <Reviews reviewsList={dummyReviews} />
                        </div>

                        <div className="user-section">
                            <h3>Favorites</h3>
                            <Favorites favoritesList={dummyFavorites} />
                        </div>
                    </div>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" className="avatar-image" />
                        <input type="file" />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <div>
                        <button type="submit">Save Changes</button>
                        <button type="button" onClick={() => setEditMode(false)}>Return to Profile</button>
                    </div>
                </form>
            )}

        </div>
    );
};

export default UserProfile;
