import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Profile.css'; // Assuming you have a CSS file for styling
import Following from '../Profile/Following';
import Followers from '../Profile/Followers';
import Reviews from '../Profile/Reviews';
import Favorites from '../Profile/Favorites';

const OtherUserProfile = () => {
    const { profileId } = useParams();
    const [userInfo, setUserInfo] = useState({
        avatar: '', // Default avatar or placeholder
        name: '',
        email: '',
        birthDate: '',
        address: ''
    });

    useEffect(() => {
        // Update the URL to match the new endpoint
        axios.get(`http://localhost:3001/profile/${profileId}`)
            .then(response => {
                setUserInfo(response.data);
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
                // Handle errors here, such as setting a state to show an error message
            });
    }, [profileId]);


    return (
        <div className="profile-container">
            <div className="user-info">
                <img src={userInfo.avatar || 'path/to/default/avatar.jpg'} alt="User Avatar" className="avatar-image" />
                <p>Name: {userInfo.name}</p>
                <p>Email: {userInfo.email}</p>
                <p>Birth Date: {userInfo.birthDate}</p>
                <p>Address: {userInfo.address}</p>
            </div>

            <div className="user-interactions-container">
                        <div className="user-section">
