import React, { useState } from 'react';

const Profile = () => {
    const [userInfo, setUserInfo] = useState({
        avatar: '', // URL of the user's avatar
        name: '', // User's name
        email: '', // Email address
        birthDate: '', // Birth date
        address: '' // Residential address
    });
    const [editMode, setEditMode] = useState(false); // State to toggle edit mode

    // Handles changes to the input fields
    const handleInputChange = (event) => {
        setUserInfo({ ...userInfo, [event.target.name]: event.target.value });
    };

    // Handles the form submission
    const handleFormSubmit = (event) => {
        event.preventDefault();
        // Logic to submit the form
        setEditMode(false); // Exit edit mode after submitting
    };

    return (
        <div className="profile-container">
            {!editMode ? (
                <>
                    <div className="user-info">
                        <img src={userInfo.avatar} alt="User Avatar" />
                        <p>Name: {userInfo.name}</p>
                        <p>Email: {userInfo.email}</p>
                        <p>Birth Date: {userInfo.birthDate}</p>
                        <p>Address: {userInfo.address}</p>
                    </div>
                    <button onClick={() => setEditMode(true)}>Edit</button>
                </>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="avatar-section">
                        <img src={userInfo.avatar} alt="User Avatar" />
                        <input type="file" />
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={userInfo.name}
                        onChange={handleInputChange}
                        placeholder="Name"
                    />
                    <input
                        type="email"
                        name="email"
                        value={userInfo.email}
                        onChange={handleInputChange}
                        placeholder="Email"
                    />
                    <input
                        type="date"
                        name="birthDate"
                        value={userInfo.birthDate}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="address"
                        value={userInfo.address}
                        onChange={handleInputChange}
                        placeholder="Address"
                    />
                    <button type="submit">Save Changes</button>
                </form>
            )}

            {/* User Cards */}
            <div className="user-cards">
                <div className="card"><a href="#"><h3>Following</h3></a></div>
                <div className="card"><a href="#"><h3>Followers</h3></a></div>
                <div className="card"><a href="#"><h3>Reviews</h3></a></div>
                <div className="card"><a href="#"><h3>Favorites</h3></a></div>
            </div>
        </div>
    );
};

export default Profile;
