import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext'; // You need to create this context

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { setAuthData } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setAuthData({
        username,
        roles: response.data.roles, // Assuming the backend sends back the roles
        token: response.data.accessToken
      });
      navigate('/dashboard'); // navigate to the dashboard or other protected route
    } catch (error) {
      alert('Failed to login');
    }
  };

  const handleGuest = () => {
    setAuthData({ username: 'guest', roles: [] });
    navigate('/dashboard'); // navigate to the dashboard or other non-protected route
  };

  const handleRegister = () => {
    navigate('/register'); // navigate to the registration page
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <button type="submit">Login</button>
      </form>
      <button onClick={handleRegister}>Register</button>
      <button onClick={handleGuest}>Continue as Guest</button>
    </div>
  );
};

export default Login;
