import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
    // Initialize authUser state from localStorage if it exists
    const [authUser, setAuthUser] = useState(() => {
      const savedUser = localStorage.getItem('authUser');
      return savedUser ? JSON.parse(savedUser) : { user: null, role: null, userID: null };
    });

  const login = (userData) => {
    setAuthUser({ 
      ...userData, // This will spread all userData properties including userID
      // If you have specific properties in userData, you can set them individually as well
      // e.g., user: userData.user, role: userData.role, userID: userData.userID
    });
    // Optionally, store user data in localStorage for persistence
  };

  const logout = () => {
    setAuthUser({
      user: null,
      role: null,
      userID: null, // Reset userID on logout
    });
    // Optionally, clear user data from localStorage
  };

  return (
    <AuthContext.Provider value={{ authUser, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
