import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


function WorkingWithArrays() {
    const [errorMessage, setErrorMessage] = useState(null);

    const API = "http://localhost:4000/a5/comments";
    const [comment, setComment] = useState({
        id: 1,
        author: "Sam",
        authorProfile: "xxxx",
        description: "Create a NodeJS server with ExpressJS",
        postId:1
      });

    //   const [comments, setComments] = useState([]);
    const [comments, setComments] = useState([{
        id: 1,
        author: "Sam",
        authorProfile: "xxxx",
        description: "Create a NodeJS server with ExpressJS",
        completed: false,
        postId:1
      }]);
      const fetchComments = async () => {
        const response = await axios.get(API);
        setComments(response.data);
      };
      useEffect(() => {
        fetchComments();
      }, []);


      const postComment = async () => {
        const response = await axios.post(API, comment);
        setComments([...comments, response.data]);
      };


      const deleteComment = async (comment) => {
        try {

        const response = await axios.delete(`${API}/${comment.id}`);
        setComments(comments.filter((t) => t.id !== comment.id));
    } catch (error) {
        console.log(error);
        setErrorMessage(error.response.data.message);
      }

    }

    //   const updateComment = async () => {

    //     try {

    //     const response = await axios.put(
    //       `${API}/${comment.id}`, comment);
    //     setComments(comments.map((t) => (
    //       t.id === comment.id ? comment : t)));
    //     setComment({});
    // } catch (error) {
    //     console.log(error);
    //     setErrorMessage(error.response.data.message);
    //   }

    //   };






    return (
      <div>

        <h1>A Post should be here by hitting Mattia API</h1>

        <textarea
        onChange={(e) => setComment({ ...comment,
          description: e.target.value })}
        value={comment.description} 
        type="text"
        className="form-control"
      />
        <button onClick={postComment}
              className="btn btn-primary mb-2 w-100">
          Create 
        </button>

        <ul className="list-group">
        {comments.map((comment) => (

          <li key={comment.id}
              className="list-group-item">
                  <button
                    onClick={() => deleteComment(comment)}
                    className="btn btn-danger float-end" >
                    Remove
                  </button>
            {comment.author}
            <a href={`${comment.authorProfile}`}
            className="btn btn-primary me-2">
            chheck author's profile
        </a>
            {comment.description}
          </li>
        ))}
      </ul>







      </div>
    );
  }
  export default WorkingWithArrays;
